import Chart from '../components/Chart'
import BasicDateRangePicker from '../components/Datepick'
import Header from '../components/Header';

import DatePicker from "react-datepicker";
import '../components/Datepick.css'
import "react-datepicker/dist/react-datepicker.css";
import {useState, useEffect} from "react";
import renderReport from '../context/report';


function ReportProduct() {

    const [data,setdata]=useState('')

    const [checkInDate, setCheckInDate] = useState(null);
    const [checkOutDate, setCheckOutDate] = useState(null);
    setdata(renderReport(checkInDate,checkOutDate))
    const handleCheckInDate = (date) => {
      setCheckInDate(date);
      setCheckOutDate(null);
    };
    const handleCheckOutDate = (date) => {
      setCheckOutDate(date);
    };


  return(

    <div class='container'>
      <Header/>
        <div className="container">
            {/* <form method="get"> */}
            <div class ='row'>
            <div class ='col-md-3 col-sm-6'>
              {/* <label>Check-in</label> */}
              <DatePicker
                selected={checkInDate}
                //minDate={new Date()}
                onChange={handleCheckInDate}
                placeholderText ="Start date"
                dateFormat="yyyy-MM-dd"
                name='start'
              />
            </div>
            <div class ='col-md-3 col-sm-6'>
              {/* <label>Check-out</label> */}
              <DatePicker
                selected={checkOutDate}
                minDate={checkInDate}
                onChange={handleCheckOutDate}
                placeholderText ="End date"
                dateFormat="yyyy-MM-dd"
                name='end'
              />
            </div>
            <div class ='col-md-3 col-sm-12'>
            <button type="button" class="btn btn-primary" onClick={RenderReport}>Xuất báo cáo</button>
            {/* <button type="submit" class="btn btn-primary">Xuất báo cáo</button> */}
            </div>
          </div>
          {/* </form> */}
        </div>
      {console.log(tienidol.data,'quanidol')}
      <Chart data={data} dataKey="Quantity" title="Sản phẩm" xaxis="Buy_Time"/>
    </div>
  )
}

export default ReportProduct;
